mapper.select:
SELECT mg.*, t.thankYouNote, #{uid} as uid FROM (select * from mmc_group where 1=1
and group_name like concat('%',#{groupName},'%')
) mg LEFT JOIN ( SELECT ui.group_id, sum(tyn.VALUE) AS thankYouNote FROM thank_you_note tyn LEFT JOIN user_info ui ON (tyn.send_user = ui.user_id) GROUP BY ui.group_id ) t on (mg.group_id = t.group_id) left join ( SELECT COUNT(*) refer, ui.group_id from referral r LEFT JOIN user_info ui on(r.send_user = ui.user_id) GROUP BY ui.group_id ) r on(r.group_id = mg.group_id)
